tinymce.PluginManager.add("directions-button",function(e){e.addButton("directions-button",{text:"",icon:"directions",onclick:function(){e.windowManager.open({title:"Add Get Directions Button",body:[{type:"textbox",name:"address",label:"Address"}],onsubmit:function(t){var n=t.data.address.replace(/[ ,.]/gi,"+");e.insertContent('<a href="https://www.google.com/maps/dir/Current+Location/'+n+'/" class="btn btn-primary"><span class="fa fa-compass"></span> Get Directions</a>',!1,!0)}})}})});